/*
===========================================
Measures Exploration
Tables Used: invoice, invoice_line, track
===========================================
*/

-- Q1: What is the total revenue generated by the store?
SELECT 
	SUM(total) AS revenue_generated 
FROM 
	invoice;

-- Q2: What is the average invoice total amount?
SELECT 
	ROUND(AVG(total), 2) AS avg_invoice_amount
FROM 
	invoice;

-- Q3: What is the price range of tracks (min, max, avg)?
SELECT 
	MIN(unit_price) AS min_track_price,
	MAX(unit_price) AS max_track_price,
	ROUND(AVG(unit_price), 2) AS avg_track_price
FROM
	track;

-- Q4: What are the longest and shortest tracks in terms of duration?
SELECT 
	name,
	milliseconds AS shortest_track
FROM 
	track
ORDER BY 
	shortest_track
LIMIT 1;

SELECT 
	name,
	milliseconds AS longest_track
FROM 
	track
ORDER BY 
	longest_track DESC
LIMIT 1;

-- Q5: What is the average length of tracks (in minutes)?
SELECT 
	ROUND(AVG(milliseconds/(100*60)),2) AS avg_length
FROM
	track;

-- Q6: What is the size range of tracks in bytes (min, max, avg)?
SELECT 
	MIN(bytes) AS min_size_bytes,
	MAX(bytes) AS max_size_bytes,
	ROUND(AVG(bytes), 2) AS avg_size_bytes
FROM 
	track;













